mapper.select:
select v.review_number, v.member_id, v.content, v.score, v.del, v.blind, v.reservation_number, v.room_number, r.reservation_number, r.studyroom_number, r.room_number from review v
left join reservation r
on v.room_number=#{roomNumber}
where v.reservation_number = r.reservation_number and v.del=0
order by review_number asc;

select v.review_number, v.member_id, v.content, v.score, v.del, v.blind, r.reservation_number, r.studyroom_number, r.room_number, r.state from review v
join reservation r
on v.member_id=#{memberId} and r.member_id=#{memberId}
where r.reservation_number=#{reservationNumber}

mapper.update:
update review
set del=#{del}
where reservation_number=#{reservationNumber}
